page.syntax-highlight:
from pytgcalls import CustomApi

api = CustomApi()

@api.on_update_custom_api()
async def custom_api_request(request: dict):
    print(request)
    return {
        'response': 'FOXES',
    }
api.start()

category.category-title:
class pytgcalls.CustomApi(
    port: int, # CustomApi port to bind the API http server
)
